  buttonHandler(){
    console.log("hello");
    this.osc = this.state.context.createOscillator();
    this.gainNode = this.state.context.createGain();
    this.osc.connect(this.gainNode);
    this.gainNode.connect(this.state.context.destination);
    this.osc.type = 'sine';
    this.osc.frequency.value = 440;
    let time = this.state.context.currentTime;
    this.gainNode.gain.setValueAtTime(1, this.state.context.currentTime);
    this.osc.start(time);
    this.gainNode.gain.exponentialRampToValueAtTime(0.001, time + 1);
    this.osc.stop(time + 1);
  }

  render() {
    return (

      <div className="App">
        <h1>hello, world!</h1>
        <button onClick={this.buttonHandler}>Play!</button>

      </div>
    );
  }
}

export default App;
