import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class synthesizer extends Component{
    constructor(){

    }
}


class synth{
    constructor(context){


    }

    mute() {//mutes the track
        console.log('muted in the sound class');
        this.gainNode.gain.setValueAtTime(0, this.context.currentTime);

    }



    unmute() {
        this.gainNode.gain.value = 1;
    }
    volumeAdjust(val) {
        this.gainNode.gain.value = val;
    }
}






class oscillatorGroup extends Component{
    /*Oscillator group is the audio source module web synthesizer. The class contains three oscillators.
    */
    constructor(context){ //context = audioContext from web audio api
        this.context = context;
        this.osc1 = new oscillator(context);
        this.osc2 = new oscillator(context);
        this.osc3 = new oscillator(context);
        this.gainNode = this.context.createGain();
        this.merge = this.context.createChannelMerger(3);
        this.osc1.getOscGainNode().connect(this.merge,1,0);
        this.osc2.connect(this.merge,1,1);
        this.osc3.connect(this.merge,1,2);
        this.merge.connect(this.gainNode);


    }
    setWave(wave,osc){
        switch (osc){
            case "osc1":
            this.osc1.setWave(wave);
            break;
            case "osc2":
            this.osc2.setWave(wave);
            break;
            case "osc3":
            this.osc3.setWave(wave);
            break;
        }
    }
    setGain(osc1Vol, osc2Vol, osc3Vol){
        //Should be a percentage, where function maps 
        this.osc1.setVolume(osc1Vol);
        this.osc2.setVolume(osc2Vol);
        this.osc3.setVolume(osc3Vol);
    }

    getlastNode(){
        return this.merge;
    }
    play(value, time){
        this.osc1.play(value, time);
        this.osc2.play(value, time);
        this.osc3.play(value, time);
    }
    stop(time){
        this.gainNode.gain.exponentialRampToValueAtTime(0.001, time + 1);
        this.osc.stop(time + 1);
    }

    render(){
        return(
        <div className="oscGroup">
            <oscillator id="osc1"></oscillator>
            <oscillator id="osc2"></oscillator>
            <oscillator id="osc3"></oscillator>
        </div>
        );
    }
}

class lfoGroup extends Component{
    constructor(context){

    }

}

class envelopeGroup extends Component{
    constructor(context){

    }
}

class oscillator extends Component{
    //oscillator class providing an interface for control used by synth.
    constructor(context){
        //context = audioContext created using the Web Audio API.
        this.context = context;
    }
    init(){
        this.osc = this.context.createOscillator();
        this.gainNode = this.context.createGain();
        this.osc.type = 'sine';
        //connects oscillator to gain node
        this.osc.connect(this.gainNode);
        //connects gain node to the output destination. Temporary until further implementation.
        this.gainNode.connect(this.context.destination);
        // This binding is necessary to make `this` work in the callback
        this.setWave = this.setWave.bind(this);
        this.getWave = this.getWave.bind(this);
        this.setWave = this.setWave.bind(this);
        this.setVolume = this.setVolume.bind(this);

    }

    setWave(wave){ 
    //sets the wave type of the oscillator
        this.osc.type = wave;
    }

    getOscGainNode(){
    //returns the oscillator object to be used to connected in an audio chain.
        return this.gainNode;
    }

    setVolume(val){
        this.gainNode.gain.value = val;
    }

    play(value, time){ 
    //value = pitch of note, time = 
        this.init();
        this.osc.frequency.value = value;
        this.gainNode.gain.setValueAtTime(1, this.context.currentTime);
        this.osc.start(time);
        this.stop(time);
    }

    stop(time) {
        this.gainNode.gain.exponentialRampToValueAtTime(0.001, time + 1);
        this.osc.stop(time + 1);
    }

    render(){
        return(
            <div id = {this.props.id}>
                <button>Power</button>
                <div className="Wave-form">
                    <button onclick={this.setWave.bind(this, 'sine')}>Sine</button>
                    <button onclick={this.setWave.bind(this, 'square')}>Square</button>
                    <button onclick={this.setWave.bind(this, 'saw')}>Saw</button>
                    <button onclick={this.setWave.bind(this, 'triangle')}>Triangle</button>
                </div>
                <div className="Pitch-control">
                    <div className="pitchKnob"></div>
                </div>
            </div>
        );
    }

}

class envelope{
    //Attack: time of gain rise - linear rate?
    //Sustain: time extension of the play trigger
    //Decay: time of gain decrease
    //Release: time of signal length from starting point
}

class lfo{
    //Frequency of low frequency oscillator
    constructor(context){
        this.osc = context.createOscillator();
        this.analyser = this.context.createAnalyser();
        this.dataArray = new Uint8Array(this.analyser.frequencyBinCount);
    }
    setWave(wave){ 
    //sets the wave type of the oscillator
        this.osc.type = wave;
    }
    getWave(){ 
    //gets the current wave type of the oscillator
        return this.osc.type;
    }
    setFreq(freq){
        this.osc.frequency.value = freq;
    }
    getFreq(){
        return this.osc.frequnect.value;
    }
    render(){
        return(
        <div id={this.props.id}>

        </div>
        )
    }
}

